import { LitElement, html, css } from 'lit';
import { customElement } from 'lit/decorators.js';
import { Router } from '@vaadin/router';
import { setLocale } from './config/localization';

// Components
import './components/nav-menu/nav-menu';
import './components/employee-list/employee-list';
import './components/employee-form/employee-form';
import './components/delete-modal/delete-modal';

@customElement('app-root')
export class App extends LitElement {
  static override styles = css`
    :host {
      display: block;
      min-height: 100vh;
      --primary-color: #E86F37;
      --secondary-color: #F5F5F5;
      --text-color: #333333;
      --border-color: #DDDDDD;
    }

    main {
      padding: 20px;
      max-width: 1200px;
      margin: 0 auto;
    }

    @media (max-width: 768px) {
      main {
        padding: 10px;
      }
    }
  `;

  override firstUpdated() {
    const router = new Router(this.renderRoot?.querySelector('#outlet'));

    router.setRoutes([
      { path: '/', redirect: '/employees' },
      { path: '/employees', component: 'employee-list' },
      { path: '/employees/add', component: 'employee-form' },
      { path: '/employees/edit/:id', component: 'employee-form' }
    ]);

    // Set initial language from HTML lang attribute
    const lang = document.documentElement.lang as 'en' | 'tr' || 'en';
    setLocale(lang);
  }

  override render() {
    return html`
      <nav-menu></nav-menu>
      <main>
        <div id="outlet"></div>
      </main>
    `;
  }
}

import { LitElement } from 'lit';

export class BaseElement extends LitElement {
  private readonly handleLanguageChange = () => this.requestUpdate();

  override connectedCallback() {
    super.connectedCallback();
    window.addEventListener('language-changed', this.handleLanguageChange);
  }

  override disconnectedCallback() {
    super.disconnectedCallback();
    window.removeEventListener('language-changed', this.handleLanguageChange);
  }
}

import { LitElement, html, css } from "lit";
import { customElement, property } from "lit/decorators.js";
import { localized, msg } from "@lit/localize";
@localized()
@customElement("delete-modal")
export class DeleteModal extends LitElement {
  @property() onConfirm = () => {};
  @property() onCancel = () => {};

  static override styles = css`
    .modal-backdrop {
      position: fixed;
      top: 0;
      left: 0;
      width: 100%;
      height: 100%;
      background: rgba(0, 0, 0, 0.5);
      display: flex;
      justify-content: center;
      align-items: center;
      z-index: 1000;
    }

    .modal {
      background: white;
      padding: 2rem;
      border-radius: 8px;
      max-width: 400px;
      width: 90%;
    }

    .modal-title {
      font-size: 1.5rem;
      margin-bottom: 1rem;
      color: var(--text-color);
    }

    .modal-content {
      margin-bottom: 1.5rem;
      color: var(--text-color);
    }

    .modal-actions {
      display: flex;
      justify-content: flex-end;
      gap: 1rem;
    }

    button {
      padding: 0.5rem 1rem;
      border-radius: 4px;
      cursor: pointer;
      border: none;
    }

    .confirm-btn {
      background: var(--primary-color);
      color: white;
    }

    .cancel-btn {
      background: var(--secondary-color);
      color: var(--text-color);
    }
  `;

  override render() {
    return html`
      <div class="modal-backdrop" @click=${this.onCancel}>
        <div class="modal" @click=${(e: Event) => e.stopPropagation()}>
          <h2 class="modal-title">${msg("Are you sure?")}</h2>
          <div class="modal-content">
            ${msg("This action cannot be undone.")}
          </div>
          <div class="modal-actions">
            <button class="cancel-btn" @click=${this.onCancel}>
              ${msg("Cancel")}
            </button>
            <button class="confirm-btn" @click=${this.onConfirm}>
              ${msg("Proceed")}
            </button>
          </div>
        </div>
      </div>
    `;
  }
}

import { html, css } from "lit";
import { customElement, property, state } from "lit/decorators.js";
import { localized, msg } from "@lit/localize";
import { Router } from "@vaadin/router";
import { store } from "../../store/store";
import { addEmployee, updateEmployee } from "../../store/actions";
import { Employee, Departments, Positions } from "../../models/employee.model";
import { Validator, ValidationError } from "../../utils/validation";
import { BaseElement } from "../base-element";
@localized()
@customElement("employee-form")
export class EmployeeForm extends BaseElement {
  @property({ type: String }) location?: string;
  @state() private employeeId: string = "";

  @state() private employee: Partial<Employee> = {
    firstName: "",
    lastName: "",
    dateOfEmployment: "",
    dateOfBirth: "",
    phoneNumber: "",
    email: "",
    department: undefined,
    position: undefined,
  };

  @state() private errors: ValidationError[] = [];
  @state() private isEdit = false;

  private unsubscribe?: () => void;

  override async connectedCallback() {
    super.connectedCallback();

    // Get ID from URL
    const path = window.location.pathname;
    const matches = path.match(/\/employees\/edit\/(.+)/);
    if (matches && matches[1]) {
      this.employeeId = matches[1];
      this.isEdit = true;

      // Get employee data from store
      const currentEmployee = store
        .getState()
        .employees.find((emp) => emp.id === this.employeeId);

      if (currentEmployee) {
        this.employee = { ...currentEmployee };
        this.requestUpdate();
      }
    }

    // Subscribe to store changes
    this.unsubscribe = store.subscribe(() => {
      if (this.isEdit) {
        const currentEmployee = store
          .getState()
          .employees.find((emp) => emp.id === this.employeeId);
        if (currentEmployee) {
          this.employee = { ...currentEmployee };
          this.requestUpdate();
        }
      }
    });
  }

  override disconnectedCallback() {
    super.disconnectedCallback();
    if (this.unsubscribe) {
      this.unsubscribe();
    }
  }

  private validateField(field: keyof Employee, value: string) {
    const validator = new Validator();

    switch (field) {
      case "email":
        validator.required(value, "Email").email(value, "Email");
        break;
      case "phoneNumber":
        validator.required(value, "Phone Number").phone(value, "Phone Number");
        break;
      case "firstName":
        validator.required(value, "First Name");
        break;
      case "lastName":
        validator.required(value, "Last Name");
        break;
      case "dateOfEmployment":
        validator
          .required(value, "Date of Employment")
          .date(value, "Date of Employment");
        break;
      case "dateOfBirth":
        validator.required(value, "Date of Birth").date(value, "Date of Birth");
        break;
      case "department":
        validator.required(value, "Department");
        break;
      case "position":
        validator.required(value, "Position");
        break;
    }

    // Remove old errors for this field
    this.errors = this.errors.filter((error) => error.field !== field);

    // Add new errors if any
    if (validator.hasErrors()) {
      this.errors = [...this.errors, ...validator.getErrors()];
    }
  }

  private handleInput(e: Event, field: keyof Employee) {
    const input = e.target as HTMLInputElement | HTMLSelectElement;
    let value = input.value;

    if (field === "phoneNumber") {
      value = value.replace(/\s/g, ""); // Remove spaces from phone number
    }

    this.employee = {
      ...this.employee,
      [field]: value,
    };

    // Validate the field after update
    this.validateField(field, value);
  }

  private handleSubmit(e: Event) {
    e.preventDefault();

    const validator = new Validator();

    // Validate all fields
    validator
      .required(this.employee.firstName, "First Name")
      .required(this.employee.lastName, "Last Name")
      .required(this.employee.dateOfEmployment, "Date of Employment")
      .required(this.employee.dateOfBirth, "Date of Birth")
      .required(this.employee.phoneNumber, "Phone Number")
      .required(this.employee.email, "Email")
      .required(this.employee.department, "Department")
      .required(this.employee.position, "Position")
      .email(this.employee.email || "", "Email")
      .phone(this.employee.phoneNumber || "", "Phone Number")
      .date(this.employee.dateOfEmployment || "", "Date of Employment")
      .date(this.employee.dateOfBirth || "", "Date of Birth");

    this.errors = validator.getErrors();

    if (validator.hasErrors()) {
      return;
    }

    if (this.isEdit && this.employeeId) {
      store.dispatch(
        updateEmployee({
          ...(this.employee as Employee),
          id: this.employeeId,
        })
      );
    } else {
      store.dispatch(addEmployee(this.employee as Employee));
    }

    Router.go("/employees");
  }

  private getFieldError(fieldName: string): string | undefined {
    return this.errors.find((error) => error.field === fieldName)?.message;
  }

  static override styles = css`
    :host {
      display: block;
      max-width: 600px;
      margin: 0 auto;
      padding: 2rem;
    }

    .form-group {
      margin-bottom: 1.5rem;
    }

    label {
      display: block;
      margin-bottom: 0.5rem;
      color: var(--text-color);
      font-weight: bold;
    }

    input,
    select {
      width: 100%;
      padding: 0.75rem;
      border: 1px solid var(--border-color);
      border-radius: 4px;
      font-size: 1rem;
    }

    input:focus,
    select:focus {
      outline: none;
      border-color: var(--primary-color);
    }

    .error {
      color: red;
      font-size: 0.875rem;
      margin-top: 0.25rem;
    }

    .form-actions {
      display: flex;
      justify-content: flex-end;
      gap: 1rem;
      margin-top: 2rem;
    }

    button {
      padding: 0.75rem 1.5rem;
      border: none;
      border-radius: 4px;
      font-size: 1rem;
      cursor: pointer;
    }

    .save-btn {
      background: var(--primary-color);
      color: white;
    }

    .cancel-btn {
      background: var(--secondary-color);
      color: var(--text-color);
    }

    @media (max-width: 768px) {
      :host {
        padding: 1rem;
      }

      .form-actions {
        flex-direction: column;
      }

      button {
        width: 100%;
      }
    }
  `;

  override render() {
    return html`
      <form @submit=${this.handleSubmit}>
        <div class="form-group">
          <label for="firstName">${msg("First Name")}</label>
          <input
            type="text"
            id="firstName"
            placeholder=${msg("Enter first name")}
            .value=${this.employee.firstName || ""}
            @input=${(e: Event) => this.handleInput(e, "firstName")}
          />
          ${this.getFieldError("First Name") &&
          html`<div class="error">
            ${this.getFieldError(msg("First Name"))!}
          </div>`}
        </div>

        <div class="form-group">
          <label for="lastName">${msg("Last Name")}</label>
          <input
            type="text"
            id="lastName"
            placeholder="Enter last name"
            .value=${this.employee.lastName || ""}
            @input=${(e: Event) => this.handleInput(e, "lastName")}
          />
          ${this.getFieldError("Last Name") &&
          html`<div class="error">${this.getFieldError("Last Name")}</div>`}
        </div>

        <div class="form-group">
          <label for="dateOfEmployment">${msg("Date of Employment")}</label>
          <input
            type="date"
            id="dateOfEmployment"
            .value=${this.employee.dateOfEmployment || ""}
            @input=${(e: Event) => this.handleInput(e, "dateOfEmployment")}
          />
          ${this.getFieldError("Date of Employment") &&
          html`<div class="error">
            ${this.getFieldError("Date of Employment")}
          </div>`}
        </div>

        <div class="form-group">
          <label for="dateOfBirth">${msg("Date of Birth")}</label>
          <input
            type="date"
            id="dateOfBirth"
            .value=${this.employee.dateOfBirth || ""}
            @input=${(e: Event) => this.handleInput(e, "dateOfBirth")}
          />
          ${this.getFieldError("Date of Birth") &&
          html`<div class="error">${this.getFieldError("Date of Birth")}</div>`}
        </div>

        <div class="form-group">
          <label for="phoneNumber">${msg("Phone Number")}</label>
          <input
            type="tel"
            id="phoneNumber"
            placeholder="+905321234567"
            .value=${this.employee.phoneNumber || ""}
            @input=${(e: Event) => {
              const input = e.target as HTMLInputElement;
              input.value = input.value.replace(/\s/g, "");
              this.handleInput(e, "phoneNumber");
            }}
          />
          ${this.getFieldError("Phone Number") &&
          html`<div class="error">${this.getFieldError("Phone Number")}</div>`}
        </div>

        <div class="form-group">
          <label for="email">${msg("Email")}</label>
          <input
            type="email"
            id="email"
            placeholder="example@company.com"
            .value=${this.employee.email || ""}
            @input=${(e: Event) => this.handleInput(e, "email")}
          />
          ${this.getFieldError("Email") &&
          html`<div class="error">${this.getFieldError("Email")}</div>`}
        </div>

        <div class="form-group">
          <label for="department">${msg("Department")}</label>
          <select
            id="department"
            .value=${this.employee.department || ""}
            @change=${(e: Event) => this.handleInput(e, "department")}
          >
            <option value="" disabled selected>Select department</option>
            ${Departments.map(
              (dept) => html` <option value=${dept}>${dept}</option> `
            )}
          </select>
          ${this.getFieldError("Department") &&
          html`<div class="error">${this.getFieldError("Department")}</div>`}
        </div>

        <div class="form-group">
          <label for="position">${msg("Position")}</label>
          <select
            id="position"
            .value=${this.employee.position || ""}
            @change=${(e: Event) => this.handleInput(e, "position")}
          >
            <option value="" disabled selected>Select position</option>
            ${Positions.map(
              (pos) => html` <option value=${pos}>${pos}</option> `
            )}
          </select>
          ${this.getFieldError("Position") &&
          html`<div class="error">${this.getFieldError("Position")}</div>`}
        </div>

        <div class="form-actions">
          <button
            type="button"
            class="cancel-btn"
            @click=${() => Router.go("/employees")}
          >
            ${msg("Cancel")}
          </button>
          <button type="submit" class="save-btn">${msg("Save")}</button>
        </div>
      </form>
    `;
  }
}

import { LitElement, html, css } from "lit";
import { customElement, state } from "lit/decorators.js";
import { localized, msg } from "@lit/localize";
import { store } from "../../store/store";
import { setViewMode, deleteEmployee } from "../../store/actions";
import { Employee } from "../../models/employee.model";
@localized()
@customElement("employee-list")
export class EmployeeList extends LitElement {
  @state() private employees: Employee[] = [];
  @state() private filteredEmployees: Employee[] = [];
  @state() private currentPage = 1;
  @state() private searchQuery = "";
  @state() private viewMode: "table" | "list" = "table";
  @state() private showDeleteModal = false;
  @state() private selectedEmployeeId: string | null = null;

  private readonly PAGE_SIZE = 10;
  private unsubscribe?: () => void;

  static override styles = css`
    :host {
      display: block;
    }

    .controls {
      display: flex;
      justify-content: space-between;
      align-items: center;
      margin-bottom: 1rem;
      gap: 1rem;
    }

    .search-box {
      flex: 1;
      max-width: 300px;
    }

    .search-box input {
      width: 100%;
      padding: 0.5rem;
      border: 1px solid var(--border-color);
      border-radius: 4px;
    }

    .view-controls button {
      padding: 0.5rem 1rem;
      background: none;
      border: 1px solid var(--border-color);
      cursor: pointer;
    }

    .view-controls button.active {
      background: var(--primary-color);
      color: white;
    }

    /* Table View Styles */
    .table-view {
      width: 100%;
      border-collapse: collapse;
    }

    .table-view th,
    .table-view td {
      padding: 1rem;
      text-align: left;
      border-bottom: 1px solid var(--border-color);
    }

    .table-view th {
      background: var(--secondary-color);
    }

    /* List View Styles */
    .list-view {
      display: grid;
      gap: 1rem;
    }

    .list-item {
      padding: 1rem;
      border: 1px solid var(--border-color);
      border-radius: 4px;
      display: grid;
      gap: 0.5rem;
    }

    .actions {
      display: flex;
      gap: 0.5rem;
    }

    .edit-btn {
      color: var(--primary-color);
      cursor: pointer;
    }

    .delete-btn {
      color: #ff4444;
      cursor: pointer;
    }

    .pagination {
      display: flex;
      justify-content: center;
      gap: 0.5rem;
      margin-top: 1rem;
    }

    .pagination button {
      padding: 0.5rem 1rem;
      border: 1px solid var(--border-color);
      background: none;
      cursor: pointer;
    }

    .pagination button.active {
      background: var(--primary-color);
      color: white;
    }

    @media (max-width: 768px) {
      .controls {
        flex-direction: column;
        align-items: stretch;
      }

      .search-box {
        max-width: none;
      }

      .table-view {
        display: none;
      }

      .list-view {
        display: grid;
      }
    }
  `;

  override connectedCallback() {
    super.connectedCallback();
    this.unsubscribe = store.subscribe(() => this.stateChanged());
    this.stateChanged();
  }

  override disconnectedCallback() {
    super.disconnectedCallback();
    this.unsubscribe?.();
  }

  private stateChanged() {
    const state = store.getState();
    this.employees = state.employees;
    this.viewMode = state.viewMode;
    this.filterEmployees();
  }

  private filterEmployees() {
    this.filteredEmployees = this.employees.filter((emp) =>
      Object.values(emp).some((value) =>
        value.toString().toLowerCase().includes(this.searchQuery.toLowerCase())
      )
    );
  }

  private handleSearch(e: InputEvent) {
    this.searchQuery = (e.target as HTMLInputElement).value;
    this.currentPage = 1;
    this.filterEmployees();
  }

  private handleViewModeChange(mode: "table" | "list") {
    store.dispatch(setViewMode(mode));
  }

  private handleDelete(id: string) {
    this.selectedEmployeeId = id;
    this.showDeleteModal = true;
  }

  private confirmDelete() {
    if (this.selectedEmployeeId) {
      store.dispatch(deleteEmployee(this.selectedEmployeeId));
      this.showDeleteModal = false;
      this.selectedEmployeeId = null;
    }
  }

  private getPaginatedEmployees() {
    const start = (this.currentPage - 1) * this.PAGE_SIZE;
    return this.filteredEmployees.slice(start, start + this.PAGE_SIZE);
  }

  private get totalPages() {
    return Math.ceil(this.filteredEmployees.length / this.PAGE_SIZE);
  }

  override render() {
    if (this.employees.length === 0) {
      return html`<p class="empty-message">${msg("No employees found")}</p>`;
    }

    const paginatedEmployees = this.getPaginatedEmployees();

    return html`
      <div class="controls">
        <div class="search-box">
          <input
            type="text"
            .value=${this.searchQuery}
            @input=${this.handleSearch}
            placeholder=${msg("Search")}
          />
        </div>
        <div class="view-controls">
          <button
            class=${this.viewMode === "table" ? "active" : ""}
            @click=${() => this.handleViewModeChange("table")}
          >
            ${msg("Table")}
          </button>
          <button
            class=${this.viewMode === "list" ? "active" : ""}
            @click=${() => this.handleViewModeChange("list")}
          >
            ${msg("List")}
          </button>
        </div>
      </div>

      ${this.viewMode === "table"
        ? this.renderTableView(paginatedEmployees)
        : this.renderListView(paginatedEmployees)}
      ${this.renderPagination()} ${this.renderDeleteModal()}
    `;
  }

  private renderTableView(employees: Employee[]) {
    return html`
      <table class="table-view">
        <thead>
          <tr>
            <th>${msg("First Name")}</th>
            <th>${msg("Last Name")}</th>
            <th>${msg("Email")}</th>
            <th>${msg("Department")}</th>
            <th>${msg("Position")}</th>
            <th>${msg("Actions")}</th>
          </tr>
        </thead>
        <tbody>
          ${employees.map((emp) => this.renderTableRow(emp))}
        </tbody>
      </table>
    `;
  }

  private renderTableRow(employee: Employee) {
    return html`
      <tr class="employee-row" data-id=${employee.id}>
        <td>${employee.firstName}</td>
        <td>${employee.lastName}</td>
        <td>${employee.email}</td>
        <td>${employee.department}</td>
        <td>${employee.position}</td>
        <td>
          <div class="actions">
            <a href="/employees/edit/${employee.id}" class="edit-btn">
              ${msg("Edit")}
            </a>
            <button
              @click=${() => this.handleDelete(employee.id)}
              class="delete-btn"
            >
              ${msg("Delete")}
            </button>
          </div>
        </td>
      </tr>
    `;
  }

  private renderListView(employees: Employee[]) {
    return html`
      <div class="list-view">
        ${employees.map(
          (emp) => html`
            <div class="list-item">
              <div><strong>${emp.firstName} ${emp.lastName}</strong></div>
              <div>${emp.department} - ${emp.position}</div>
              <div>${emp.dateOfEmployment}</div>
              <div class="actions">
                <span
                  class="edit-btn"
                  @click=${() =>
                    (window.location.href = `/employees/edit/${emp.id}`)}
                  ></span
                >
                <span
                  class="delete-btn"
                  @click=${() => this.handleDelete(emp.id)}
                  ></span
                >
              </div>
            </div>
          `
        )}
      </div>
    `;
  }

  private renderPagination() {
    return html`
      <div class="pagination">
        <button
          ?disabled=${this.currentPage === 1}
          @click=${() => this.currentPage--}
        >

        </button>
        ${Array.from({ length: this.totalPages }, (_, i) => i + 1).map(
          (page) => html`
            <button
              class=${page === this.currentPage ? "active" : ""}
              @click=${() => (this.currentPage = page)}
            >
              ${page}
            </button>
          `
        )}
        <button
          ?disabled=${this.currentPage === this.totalPages}
          @click=${() => this.currentPage++}
        >

        </button>
      </div>
    `;
  }

  private renderDeleteModal() {
    if (!this.showDeleteModal) return null;

    return html`
      <delete-modal
        .onConfirm=${() => this.confirmDelete()}
        .onCancel=${() => (this.showDeleteModal = false)}
      ></delete-modal>
    `;
  }
}

import { $, expect } from "@wdio/globals";
import { NavMenu } from "./nav-menu"; // Your NavMenu component
import { html, render } from "lit";
import sinon from "sinon"; // Sinon for spies
import { AppState } from "../../store/reducers";
import { store } from "../../store/store";

describe("Lit component testing", () => {
  let requestUpdateSpy: sinon.SinonSpy;

  beforeEach(async () => {
    // Mock the store state and subscribe method
    render(html`<nav-menu id="root"></nav-menu>`, document.body);
    sinon.stub(store, "getState").returns({ language: "en" } as AppState); // Initial state with 'en' language
    sinon.stub(store, "subscribe").callsFake((callback) => {
      // Simulate store subscription and trigger the callback immediately
      callback();
      return () => {}; // Return unsubscribe function
    });

    // Get the instance of the Lit component
    const element = (await document.getElementById("root")) as NavMenu;
    requestUpdateSpy = sinon.spy(element, "requestUpdate");
